var verifyAuth = function verifyAuth(req, res, next) {
  var token = req.body.token;
  jwt.verify(token, "secret", function (error, decoded) {
    if (error) {
      return res.status(401).json({
        mensaje: "Error de token",
        error: error
      });
    } // Creamos una nueva propiedad con la info del usuario


    req.usuario = decoded.data; //data viene al generar el token en login.js

    console.log(req.usuario);
    next();
  });
};

var verifyRol = function verifyRol(req, res, next) {
  var rol = req.usuario.role;
  console.log(req.usuario);

  if (rol !== "ADMIN") {
    return res.status(401).json({
      mensaje: "Rol no autorizado!"
    });
  }

  next();
};

module.exports = {
  verifyAuth: verifyAuth,
  verifyRol: verifyRol
};
